export default function AdminManagementPage() {
  const [admins, setAdmins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [formData, setFormData] = useState({
    full_name: "",
    email: "",
    password: "",
    phone: "",
    role: "admin"
  });
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });

  useEffect(() => {
    fetchAdmins();
  }, []);

  async function fetchAdmins() {
    try {
      const res = await fetch("/api/admin/users?role=admin");
      const data = await res.json();
      setAdmins(data.users || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  function openCreateModal() {
    setEditingUser(null);
    setFormData({
      full_name: "",
      email: "",
      password: "",
      phone: "",
      role: "admin"
    });
    setShowModal(true);
  }

  function openEditModal(user) {
    setEditingUser(user);
    setFormData({
      full_name: user.full_name,
      email: user.email,
      password: "",
      phone: user.phone || "",
      role: "admin"
    });
    setShowModal(true);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setSaving(true);
    setMessage({ type: "", text: "" });

    try {
      if (editingUser) {
        const updateData = { ...formData };
        if (!updateData.password) delete updateData.password;
        
        const res = await fetch(`/api/admin/users/${editingUser.id}`, {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updateData)
        });
        const data = await res.json();
        
        if (!res.ok) throw new Error(data.error);
        setMessage({ type: "success", text: "C·∫≠p nh·∫≠t th√†nh c√¥ng!" });
      } else {
        const res = await fetch("/api/admin/users", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData)
        });
        const data = await res.json();
        
        if (!res.ok) throw new Error(data.error);
        setMessage({ type: "success", text: "T·∫°o t√†i kho·∫£n th√†nh c√¥ng!" });
      }
      
      fetchAdmins();
      setTimeout(() => setShowModal(false), 1000);
    } catch (err) {
      setMessage({ type: "error", text: err.message });
    } finally {
      setSaving(false);
    }
  }

  async function handleDelete(user) {
    if (!confirm(`B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a admin "${user.full_name}"?`)) return;
    
    try {
      const res = await fetch(`/api/admin/users/${user.id}`, { method: "DELETE" });
      const data = await res.json();
      
      if (!res.ok) throw new Error(data.error);
      fetchAdmins();
    } catch (err) {
      alert(err.message);
    }
  }

  async function toggleStatus(user) {
    const newStatus = user.status === "active" ? "blocked" : "active";
    try {
      await fetch(`/api/admin/users/${user.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus })
      });
      fetchAdmins();
    } catch (err) {
      alert(err.message);
    }
  }

  const filteredAdmins = admins.filter(a =>
    a.full_name.toLowerCase().includes(search.toLowerCase()) ||
    a.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
      <div className="admin-page">
        <div className="admin-page__header">
          <h1 className="admin-page__title">Qu·∫£n l√Ω Admin</h1>
          <button className="btn btn-primary" onClick={openCreateModal}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <line x1="12" y1="5" x2="12" y2="19"></line>
              <line x1="5" y1="12" x2="19" y2="12"></line>
            </svg>
            Th√™m Admin
          </button>
        </div>

        <div className="admin-filters">
          <input
            type="search"
            placeholder="T√¨m ki·∫øm admin..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="admin-search"
          />
        </div>

        {loading ? (
          <div className="admin-loading">ƒêang t·∫£i...</div>
        ) : (
          <div className="admin-table-wrap">
            <table className="admin-table">
              <thead>
                <tr>
                  <th>Admin</th>
                  <th>Email</th>
                  <th>S·ªë ƒëi·ªán tho·∫°i</th>
                  <th>Tr·∫°ng th√°i</th>
                  <th>Ng√†y t·∫°o</th>
                  <th>Thao t√°c</th>
                </tr>
              </thead>
              <tbody>
                {filteredAdmins.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="admin-empty">Ch∆∞a c√≥ admin n√†o</td>
                  </tr>
                ) : (
                  filteredAdmins.map(user => (
                    <tr key={user.id}>
                      <td>
                        <div className="admin-user-cell">
                          <div className="admin-avatar admin-avatar--admin">
                            {user.avatar_url ? (
                              <img src={user.avatar_url} alt="" />
                            ) : (
                              user.full_name.charAt(0).toUpperCase()
                            )}
                          </div>
                          <span>{user.full_name}</span>
                        </div>
                      </td>
                      <td>{user.email}</td>
                      <td>{user.phone || "-"}</td>
                      <td>
                        <span className={`admin-badge admin-badge--${user.status}`}>
                          {user.status === "active" ? "Ho·∫°t ƒë·ªông" : "Kh√≥a"}
                        </span>
                      </td>
                      <td>{new Date(user.created_at).toLocaleDateString("vi-VN")}</td>
                      <td>
                        <div className="admin-actions">
                          <button
                            className="admin-action-btn"
                            title="S·ª≠a"
                            onClick={() => openEditModal(user)}
                          >
                            ‚úèÔ∏è
                          </button>
                          <button
                            className="admin-action-btn"
                            title={user.status === "active" ? "Kh√≥a" : "M·ªü kh√≥a"}
                            onClick={() => toggleStatus(user)}
                          >
                            {user.status === "active" ? "üîí" : "üîì"}
                          </button>
                          <button
                            className="admin-action-btn admin-action-btn--danger"
                            title="X√≥a"
                            onClick={() => handleDelete(user)}
                          >
                            üóëÔ∏è
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* Modal */}
        {showModal && (
          <div className="admin-modal-overlay" onClick={() => setShowModal(false)}>
            <div className="admin-modal" onClick={e => e.stopPropagation()}>
              <div className="admin-modal__header">
                <h2>{editingUser ? "S·ª≠a Admin" : "Th√™m Admin m·ªõi"}</h2>
                <button className="admin-modal__close" onClick={() => setShowModal(false)}>√ó</button>
              </div>
              
              <form onSubmit={handleSubmit} className="admin-modal__body">
                {message.text && (
                  <div className={`admin-message admin-message--${message.type}`}>
                    {message.text}
                  </div>
                )}
                
                <div className="admin-form-group">
                  <label>H·ªç t√™n *</label>
                  <input
                    type="text"
                    value={formData.full_name}
                    onChange={e => setFormData({...formData, full_name: e.target.value})}
                    required
                  />
                </div>
                
                <div className="admin-form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    value={formData.email}
                    onChange={e => setFormData({...formData, email: e.target.value})}
                    required
                    disabled={!!editingUser}
                  />
                </div>
                
                <div className="admin-form-group">
                  <label>{editingUser ? "M·∫≠t kh·∫©u m·ªõi (ƒë·ªÉ tr·ªëng n·∫øu kh√¥ng ƒë·ªïi)" : "M·∫≠t kh·∫©u *"}</label>
                  <input
                    type="password"
                    value={formData.password}
                    onChange={e => setFormData({...formData, password: e.target.value})}
                    required={!editingUser}
                    minLength={6}
                  />
                </div>
                
                <div className="admin-form-group">
                  <label>S·ªë ƒëi·ªán tho·∫°i</label>
                  <input
                    type="tel"
                    value={formData.phone}
                    onChange={e => setFormData({...formData, phone: e.target.value})}
                  />
                </div>

                <div className="admin-modal__footer">
                  <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                    H·ªßy
                  </button>
                  <button type="submit" className="btn btn-primary" disabled={saving}>
                    {saving ? "ƒêang l∆∞u..." : (editingUser ? "C·∫≠p nh·∫≠t" : "T·∫°o t√†i kho·∫£n")}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
  );
}





