import { useState } from "react";
import { useRouter } from "next/navigation";
import Header from "@/components/Header";
import Footer from "@/components/Footer";
import Link from "next/link";

export default function ForgotPasswordPage() {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [step, setStep] = useState(1); // 1: nháº­p email, 2: Ä‘Ã£ gá»­i email

  const validateEmail = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email.trim()) {
      setEmailError("Vui lÃ²ng nháº­p email");
      return false;
    } else if (!emailRegex.test(email)) {
      setEmailError("Email khÃ´ng há»£p lá»‡");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setEmailError("");
    setMessage("");

    if (!validateEmail()) return;

    setIsLoading(true);

    try {
      const response = await fetch("/api/auth/forgot-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (data.success) {
        setStep(2);
        setMessage(data.message);
      } else {
        setMessage(data.message || "CÃ³ lá»—i xáº£y ra");
      }
    } catch (error) {
      setMessage("KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n server. Vui lÃ²ng thá»­ láº¡i.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <Header />
      <main className="login-container">
        <div className="login-box">
          {step === 1 ? (
            <>
              <h2>QuÃªn máº­t kháº©u</h2>
              <p style={{ color: "var(--text-muted)", marginBottom: "24px" }}>
                Nháº­p email cá»§a báº¡n Ä‘á»ƒ nháº­n link Ä‘áº·t láº¡i máº­t kháº©u
              </p>

              {message && (
                <div className={`general-error ${message.includes("thÃ nh cÃ´ng") || message.includes("gá»­i") ? "success" : ""}`}>
                  {message}
                </div>
              )}

              <form onSubmit={handleSubmit} autoComplete="off">
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    id="email"
                    type="email"
                    placeholder="Nháº­p email Ä‘Äƒng kÃ½"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    disabled={isLoading}
                    autoFocus
                  />
                  {emailError && <div className="error-message">{emailError}</div>}
                </div>

                <button type="submit" className="btn-login" disabled={isLoading}>
                  {isLoading ? "Äang gá»­i..." : "Gá»­i link Ä‘áº·t láº¡i máº­t kháº©u"}
                </button>
              </form>

              <p className="register-text">
                Nhá»› máº­t kháº©u? <Link href="/login">ÄÄƒng nháº­p</Link>
              </p>
            </>
          ) : (
            <>
              <div style={{ textAlign: "center" }}>
                <div style={{ fontSize: "64px", marginBottom: "20px" }}>ğŸ“§</div>
                <h2>ÄÃ£ gá»­i email!</h2>
                <p style={{ color: "var(--text-muted)", margin: "20px 0" }}>
                  ChÃºng tÃ´i Ä‘Ã£ gá»­i link Ä‘áº·t láº¡i máº­t kháº©u Ä‘áº¿n email <strong>{email}</strong>
                </p>
                <p style={{ color: "var(--text-soft)", fontSize: "0.9rem", marginBottom: "30px" }}>
                  Vui lÃ²ng kiá»ƒm tra há»™p thÆ° (cáº£ thÆ° má»¥c spam) vÃ  lÃ m theo hÆ°á»›ng dáº«n trong email.
                </p>
                <p style={{ color: "var(--text-soft)", fontSize: "0.85rem", marginBottom: "30px" }}>
                  Link sáº½ háº¿t háº¡n sau 1 giá».
                </p>
                <div style={{ display: "flex", gap: "12px", justifyContent: "center", flexWrap: "wrap" }}>
                  <button
                    className="btn-login"
                    onClick={() => {
                      setStep(1);
                      setEmail("");
                      setMessage("");
                    }}
                  >
                    Gá»­i láº¡i email
                  </button>
                  <Link href="/login" className="btn-login" style={{ textDecoration: "none", display: "inline-block" }}>
                    Vá» trang Ä‘Äƒng nháº­p
                  </Link>
                </div>
              </div>
            </>
          )}
        </div>
      </main>
      <Footer />
    </>
  );
}














